IndexItem.Content:
MA5:MA(CLOSE, M1);
MA10:MA(CLOSE, M2);
MA20:MA(CLOSE, M3);
MA30:MA(CLOSE, M4);
MA60:MA(CLOSE, M5);
MA120:MA(CLOSE, M6);

LLVN:=LLV(LOW,N);
HIGHN:=HHV(HIGH,N);
RSV:=(CLOSE-LLVN)/(HIGHN-LLVN)*100;
K:SMA(RSV,M1,1);
D:SMA(K,M2,1);
J:3*K-2*D;

LC:=REF(CLOSE,1);
SMA1:=SMA(MAX(CLOSE-LC,0),N1,1);
SMAABS1:=SMA(ABS(CLOSE-LC),N1,1);
SMA2:=SMA(MAX(CLOSE-LC,0),N2,1);
SMAABS2:=SMA(ABS(CLOSE-LC),N2,1);
SMA3:=SMA(MAX(CLOSE-LC,0),N3,1);
SMAABS3:=SMA(ABS(CLOSE-LC),N3,1);
RSI1:SMA1/SMAABS1*100;
RSI2:SMA2/SMAABS2*100;
RSI3:SMA3/SMAABS3*100;

MTR:=EXPMEMA(MAX(MAX(HIGH-LOW,ABS(HIGH-REF(CLOSE,1))),ABS(REF(CLOSE,1)-LOW)),N);
HD :=HIGH-REF(HIGH,1);
LD :=REF(LOW,1)-LOW;
DMP:=EXPMEMA(IF(HD>0&&HD>LD,HD,0),N);
DMM:=EXPMEMA(IF(LD>0&&LD>HD,LD,0),N);
PDI: DMP*100/MTR;
MDI: DMM*100/MTR;
ADXEXP:=ABS(MDI-PDI)/(MDI+PDI)*100;
ADX:EXPMEMA(ADXEXP,MM);
ADXR:EXPMEMA(ADX,MM);

MA1:=MA(CLOSE,N1);
MA2:=MA(CLOSE,N2);
MA3:=MA(CLOSE,N3);
BIAS1:(CLOSE-MA1)/MA1*100;
BIAS2:(CLOSE-MA2)/MA2*100;
BIAS3:(CLOSE-MA3)/MA3*100;

LC:=REF(CLOSE,1);AA:=ABS(HIGH-LC);
BB:=ABS(LOW-LC);
CC:=ABS(HIGH-REF(LOW,1));
DD:=ABS(LC-REF(OPEN,1));
R:=IF(AA>BB AND AA>CC,AA+BB/2+DD/4,IF(BB>CC AND BB>AA,BB+AA/2+DD/4,CC+DD/4));
X:=(CLOSE-LC+(CLOSE-OPEN)/2+LC-REF(OPEN,1));
SI:=16*X/R*MAX(AA,BB);
ASI:SUM(SI,0);
MASI:MA(ASI,M);
